import React from 'react';
import { Activity, MessageSquare, Terminal, Users } from 'lucide-react';

const StatCard = ({ icon: Icon, label, value, color }) => (
  <div className="bg-slate-800/30 p-4 rounded-2xl border border-white/5 flex flex-col gap-2">
    <Icon size={18} className={color} />
    <span className="text-xs text-slate-400 uppercase font-bold">{label}</span>
    <span className="text-xl font-mono text-white">{value}</span>
  </div>
);

const UserStats = ({ user }) => {
  if (!user) return (
    <div className="h-full flex flex-col items-center justify-center text-slate-500 opacity-50">
      <Activity size={48} className="mb-4 animate-pulse" />
      <p>Select an engineer to audit</p>
    </div>
  );

  return (
    <div className="animate-in slide-in-from-bottom-4 duration-500">
      <div className="mb-8">
        <h3 className="text-3xl font-black text-white">{user.name}</h3>
        <span className="px-3 py-1 bg-emerald-500/10 text-emerald-400 text-xs font-bold rounded-full border border-emerald-500/20">
          {user.imp_z > 0 && user.vis_z < 0 ? 'Verified Silent Architect' : 'Core Contributor'}
        </span>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <StatCard icon={Terminal} label="Commits" value={user.commits} color="text-blue-400" />
        <StatCard icon={Activity} label="Complexity" value={user.entropy.toFixed(2)} color="text-purple-400" />
        <StatCard icon={MessageSquare} label="Signal" value={user.comm_score.toFixed(1)} color="text-emerald-400" />
        <StatCard icon={Users} label="Meetings" value={`${user.meetings}h`} color="text-amber-400" />
      </div>
      
      <div className="mt-8 p-4 bg-blue-600/10 rounded-2xl border border-blue-500/20">
        <p className="text-xs text-blue-300 italic">
          "This user has a high impact-to-visibility ratio, indicating potential under-recognition in standard peer evaluations."
        </p>
      </div>
    </div>
  );
};

export default UserStats;
