import React, { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import UserStats from './components/UserStats';

function App() {
  const [data, setData] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/dashboard')
      .then(res => res.json())
      .then(json => {
        setData(json);
        if (json.length > 0) setSelectedUser(json[0]);
      });
  }, []);

  return (
    <div className="flex h-screen bg-[#020617] text-slate-200 overflow-hidden font-sans">
      <Sidebar />
      
      <main className="flex-1 p-8 overflow-y-auto">
        <header className="flex justify-between items-end mb-12">
          <div>
            <h1 className="text-5xl font-black text-white tracking-tighter">DEVLENS<span className="text-blue-500">.</span></h1>
            <p className="text-slate-400 text-lg mt-2 font-light">Advanced Developer Performance Analytics Platform</p>
          </div>
          <div className="bg-slate-900 border border-white/10 px-6 py-3 rounded-xl shadow-xl">
            <span className="text-xs text-slate-500 uppercase font-semibold block">System Status</span>
            <span className="text-emerald-400 font-mono text-sm">Active â€¢ Real-time Analytics</span>
          </div>
        </header>

        <div className="grid grid-cols-1 xl:grid-cols-4 gap-8">
          <div className="xl:col-span-3">
            <Dashboard data={data} onUserSelect={setSelectedUser} />
          </div>
          <div className="xl:col-span-1 bg-slate-900/50 backdrop-blur-xl p-8 rounded-3xl border border-white/10 shadow-2xl">
            <UserStats user={selectedUser} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
