import React, { useState, useEffect } from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, Tooltip, ResponsiveContainer, ReferenceLine, Cell } from 'recharts';
import { BarChart3, TrendingUp, Eye, EyeOff } from 'lucide-react';

const Dashboard = ({ data, onUserSelect, settings }) => {
  const [hiddenGems, setHiddenGems] = useState([]);
  const [showingHiddenGems, setShowingHiddenGems] = useState(true);

  // Filter Hidden Gems (Quadrant 2 - High Impact, Low Visibility)
  useEffect(() => {
    const gems = data.filter(dev => dev.is_hidden_gem === true);
    gems.sort((a, b) => b.raw_impact - a.raw_impact); // Sort by impact score
    setHiddenGems(gems);
  }, [data]);

  // Sorting data by Impact Z-score for alternative view
  const leaderboard = [...data].sort((a, b) => b.imp_z - a.imp_z);

  const getColor = (node) => {
    // Color coding based on quadrants
    if (node.quadrant === 1) return '#16a34a'; // Stars - Green
    if (node.quadrant === 2) return '#f59e0b'; // Hidden Gems - Amber
    if (node.quadrant === 3) return '#3b82f6'; // Communicators - Blue
    return '#6b7280'; // Needs Support - Gray
  };

  const getQuadrantLabel = (quadrant) => {
    switch(quadrant) {
      case 1: return "Star";
      case 2: return "Hidden Gem";
      case 3: return "Connector";
      case 4: return "Developing";
      default: return "Unknown";
    }
  };

  const animationClass = settings?.dashboard?.showAnimations ? 'transition-all duration-200' : '';

  const displayData = showingHiddenGems ? hiddenGems : leaderboard;
  const sectionTitle = showingHiddenGems ? "Hidden Gems" : "Performance Rankings";
  const sectionSubtitle = showingHiddenGems 
    ? "High-impact developers who deserve more recognition" 
    : "Overall performance rankings by impact score";

  return (
    <div className="flex gap-6 h-full">
      {/* Performance Matrix Visualization */}
      <div className={`flex-[3] bg-gray-50 p-6 rounded-xl border border-gray-200 shadow-sm ${animationClass}`}>
        <h2 className="text-xl font-semibold text-black mb-6 flex items-center gap-3">
          <BarChart3 className="text-green-600" size={20} /> 
          Performance Impact Matrix
        </h2>
        
        {/* Quadrant Legend */}
        <div className="mb-4 flex flex-wrap gap-4 text-xs">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-green-600 rounded-full"></div>
            <span>Stars (High Impact, High Visibility)</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-amber-500 rounded-full"></div>
            <span>Hidden Gems (High Impact, Low Visibility)</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
            <span>Connectors (Low Impact, High Visibility)</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-gray-500 rounded-full"></div>
            <span>Developing (Low Impact, Low Visibility)</span>
          </div>
        </div>

        <div className="h-[450px] w-full">
          <ResponsiveContainer>
            <ScatterChart margin={{ top: 20, right: 30, bottom: 20, left: 10 }}>
              <XAxis 
                type="number" 
                dataKey="vis_z" 
                name="Visibility" 
                stroke="#6b7280" 
                tick={{fill: '#6b7280'}} 
                label={{ value: 'Visibility Score', position: 'bottom', fill: '#6b7280', fontSize: 12 }} 
              />
              <YAxis 
                type="number" 
                dataKey="imp_z" 
                name="Impact" 
                stroke="#6b7280" 
                tick={{fill: '#6b7280'}} 
                label={{ value: 'Impact Score', angle: -90, position: 'left', fill: '#6b7280', fontSize: 12 }} 
              />
              <Tooltip 
                cursor={{ strokeDasharray: '3 3', stroke: '#16a34a', strokeWidth: 1 }} 
                contentStyle={{ 
                  backgroundColor: 'white', 
                  border: '1px solid #d1d5db', 
                  borderRadius: '8px', 
                  color: 'black',
                  fontSize: '12px',
                  boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                }}
                formatter={(value, name) => [value?.toFixed(2), name === 'vis_z' ? 'Visibility' : 'Impact']}
                labelFormatter={(label, payload) => {
                  if (payload && payload[0]) {
                    const dev = payload[0].payload;
                    return `${dev.name} (${getQuadrantLabel(dev.quadrant)})`;
                  }
                  return label;
                }}
              />
              <ReferenceLine x={0} stroke="#d1d5db" strokeDasharray="2 2" />
              <ReferenceLine y={0} stroke="#d1d5db" strokeDasharray="2 2" />
              <Scatter data={data} onClick={(e) => onUserSelect(e.payload)}>
                {data.map((entry, index) => (
                  <Cell 
                    key={`cell-${index}`} 
                    fill={getColor(entry)} 
                    className="cursor-pointer hover:opacity-80" 
                    style={{ filter: 'none' }}
                  />
                ))}
              </Scatter>
            </ScatterChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Hidden Gems / Performance Rankings */}
      <div className={`flex-1 bg-gray-50 p-6 rounded-xl border border-gray-200 overflow-y-auto ${animationClass}`}>
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-xl font-semibold text-black flex items-center gap-3">
              <TrendingUp className="text-green-600" size={20} /> 
              {sectionTitle}
            </h2>
            <p className="text-sm text-gray-600 mt-1">{sectionSubtitle}</p>
          </div>
          <button
            onClick={() => setShowingHiddenGems(!showingHiddenGems)}
            className="flex items-center gap-2 px-3 py-2 text-sm bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
            title={showingHiddenGems ? "Show all rankings" : "Show hidden gems only"}
          >
            {showingHiddenGems ? <Eye size={16} /> : <EyeOff size={16} />}
            {showingHiddenGems ? "All" : "Gems"}
          </button>
        </div>

        <div className="space-y-3">
          {displayData.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              {showingHiddenGems ? "No hidden gems found" : "No data available"}
            </div>
          ) : (
            displayData.map((user, index) => (
              <div 
                key={user.name} 
                onClick={() => onUserSelect(user)}
                className={`group flex items-center justify-between p-4 rounded-lg bg-white hover:bg-green-50 border border-gray-200 hover:border-green-300 cursor-pointer ${animationClass}`}
              >
                <div className="flex items-center gap-3">
                  <span className={`text-sm font-semibold min-w-[24px] ${index < 3 ? 'text-green-600' : 'text-gray-500'}`}>
                    #{index + 1}
                  </span>
                  <div>
                    <div className="text-black font-medium text-sm flex items-center gap-2">
                      {user.name}
                      {showingHiddenGems && (
                        <span className="px-2 py-1 text-xs bg-amber-100 text-amber-800 rounded-full">
                          Hidden Gem
                        </span>
                      )}
                    </div>
                    <div className="text-xs text-gray-500">
                      {showingHiddenGems 
                        ? `Impact: ${user.raw_impact?.toFixed(2)} â€¢ Visibility: ${user.raw_visibility?.toFixed(2)}`
                        : `Impact Score: ${user.imp_z?.toFixed(2)}`
                      }
                    </div>
                    {showingHiddenGems && (
                      <div className="text-xs text-gray-400 mt-1">
                        {user.commits} commits â€¢ {user.team} team
                      </div>
                    )}
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  {!showingHiddenGems && user.quadrant && (
                    <div 
                      className="w-3 h-3 rounded-full" 
                      style={{ backgroundColor: getColor(user) }}
                      title={getQuadrantLabel(user.quadrant)}
                    ></div>
                  )}
                  {(showingHiddenGems ? user.raw_impact > 2 : user.imp_z > 1) && (
                    <div className="w-2 h-2 bg-green-600 rounded-full"></div>
                  )}
                </div>
              </div>
            ))
          )}
        </div>

        {showingHiddenGems && hiddenGems.length > 0 && (
          <div className="mt-6 p-4 bg-amber-50 border border-amber-200 rounded-lg">
            <h3 className="font-medium text-amber-800 mb-2">ðŸ’¡ Hidden Gems Insights</h3>
            <p className="text-sm text-amber-700">
              These developers have high technical impact but low visibility. Consider:
            </p>
            <ul className="text-xs text-amber-600 mt-2 space-y-1">
              <li>â€¢ Highlighting their contributions in team meetings</li>
              <li>â€¢ Encouraging them to share knowledge and mentor others</li>
              <li>â€¢ Providing opportunities for public recognition</li>
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
