import React from 'react';
import { Activity, MessageSquare, Terminal, Users, User } from 'lucide-react';

const StatCard = ({ icon: Icon, label, value, color }) => (
  <div className="bg-slate-800/30 p-4 rounded-lg border border-white/5 flex flex-col gap-2">
    <Icon size={16} className={color} />
    <span className="text-xs text-slate-400 uppercase font-medium tracking-wide">{label}</span>
    <span className="text-lg font-semibold text-white font-mono">{value}</span>
  </div>
);

const UserStats = ({ user }) => {
  if (!user) return (
    <div className="h-full flex flex-col items-center justify-center text-slate-400">
      <User size={48} className="mb-4 opacity-50" />
      <p className="text-sm font-medium">Select a developer to view analytics</p>
    </div>
  );

  const getPerformanceCategory = (user) => {
    if (user.imp_z > 0 && user.vis_z < 0) return 'High Impact Contributor';
    if (user.imp_z > 0 && user.vis_z > 0) return 'Visible High Performer';
    if (user.imp_z > 0) return 'Above Average Performer';
    return 'Standard Contributor';
  };

  const getCategoryColor = (user) => {
    if (user.imp_z > 0 && user.vis_z < 0) return 'bg-emerald-500/10 text-emerald-400 border-emerald-500/20';
    if (user.imp_z > 0 && user.vis_z > 0) return 'bg-blue-500/10 text-blue-400 border-blue-500/20';
    if (user.imp_z > 0) return 'bg-purple-500/10 text-purple-400 border-purple-500/20';
    return 'bg-slate-500/10 text-slate-400 border-slate-500/20';
  };

  return (
    <div className="animate-in slide-in-from-bottom-4 duration-300">
      <div className="mb-8">
        <h3 className="text-2xl font-semibold text-white mb-3">{user.name}</h3>
        <span className={`px-3 py-1 text-xs font-medium rounded-full border ${getCategoryColor(user)}`}>
          {getPerformanceCategory(user)}
        </span>
      </div>

      <div className="grid grid-cols-2 gap-4 mb-6">
        <StatCard 
          icon={Terminal} 
          label="Commits" 
          value={user.commits} 
          color="text-blue-400" 
        />
        <StatCard 
          icon={Activity} 
          label="Complexity" 
          value={user.entropy.toFixed(2)} 
          color="text-purple-400" 
        />
        <StatCard 
          icon={MessageSquare} 
          label="Communication" 
          value={user.comm_score.toFixed(1)} 
          color="text-emerald-400" 
        />
        <StatCard 
          icon={Users} 
          label="Meetings" 
          value={`${user.meetings}h`} 
          color="text-amber-400" 
        />
      </div>
      
      <div className="p-4 bg-slate-800/20 rounded-lg border border-slate-700/50">
        <h4 className="text-sm font-medium text-slate-300 mb-2">Performance Analysis</h4>
        <p className="text-xs text-slate-400 leading-relaxed">
          Impact Score: {user.imp_z.toFixed(2)} | Visibility Score: {user.vis_z.toFixed(2)}
        </p>
        <p className="text-xs text-slate-400 leading-relaxed mt-2">
          This developer demonstrates {user.imp_z > 0 ? 'above-average' : 'standard'} impact metrics 
          with {user.vis_z > 0 ? 'high' : 'moderate'} visibility in team communications.
        </p>
      </div>
    </div>
  );
};

export default UserStats;
