import React, { createContext, useContext, useState, useEffect } from 'react';

const AppContext = createContext();

export const useApp = () => {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error('useApp must be used within an AppProvider');
  }
  return context;
};

export const AppProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [settings, setSettings] = useState(null);

  // Load user and settings from localStorage
  useEffect(() => {
    const savedUser = localStorage.getItem('devlens-user');
    const savedSettings = localStorage.getItem('devlens-settings');
    
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    
    if (savedSettings) {
      setSettings(JSON.parse(savedSettings));
    } else {
      // Default settings
      const defaultSettings = {
        notifications: {
          emailAlerts: true,
          performanceAlerts: true,
          weeklyReports: false,
          teamUpdates: true,
          criticalIssues: true,
          emailAddress: ''
        },
        dashboard: {
          refreshInterval: '5',
          defaultView: 'dashboard',
          showAnimations: true,
          compactMode: false,
          darkMode: false
        },
        analytics: {
          trackingPeriod: '30',
          minCommitsThreshold: '1',
          impactCalculation: 'weighted',
          excludeWeekends: false,
          includeDocumentation: true
        },
        team: {
          autoAssignTeams: true,
          allowTeamSwitching: false,
          requireApproval: true,
          showSensitiveData: false
        },
        privacy: {
          anonymizeData: false,
          retentionPeriod: '365',
          shareAnalytics: false,
          exportEnabled: true
        }
      };
      setSettings(defaultSettings);
      localStorage.setItem('devlens-settings', JSON.stringify(defaultSettings));
    }
  }, []);

  const login = async (email, password, company) => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password, company }),
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        localStorage.setItem('devlens-user', JSON.stringify(data.user));
        return { success: true };
      } else {
        const error = await response.json();
        return { success: false, error: error.detail };
      }
    } catch (error) {
      return { success: false, error: 'Network error' };
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('devlens-user');
    localStorage.removeItem('devlens-settings');
    setSettings(null);
  };

  const updateSettings = (newSettings) => {
    setSettings(newSettings);
    localStorage.setItem('devlens-settings', JSON.stringify(newSettings));
  };

  // Mock data based on company
  const getMockData = () => {
    if (!user) return [];

    const companyData = {
      'TechCorp Inc.': [
        {"name": "Alex", "team": "Backend", "commits": 14, "entropy": 0.95, "meetings": 2, "msgs": ["Fixed memory leak", "Updated API logic"]},
        {"name": "Jordan", "team": "Frontend", "commits": 2, "entropy": 0.20, "meetings": 15, "msgs": ["Morning!", "Coffee?", "Great demo everyone", "Let's sync up"]},
        {"name": "Sam", "team": "Backend", "commits": 18, "entropy": 0.85, "meetings": 10, "msgs": ["Merged hotfix", "Checking logs", "github.com/pull/42"]},
        {"name": "Taylor", "team": "Frontend", "commits": 4, "entropy": 0.40, "meetings": 3, "msgs": ["Learning docs", "Help with setup?"]},
        {"name": "Casey", "team": "DevOps", "commits": 12, "entropy": 0.75, "meetings": 1, "msgs": ["Refactoring middle-layer", "DB migration done"]},
        {"name": "Morgan", "team": "Backend", "commits": 8, "entropy": 0.60, "meetings": 5, "msgs": ["Code review complete", "Testing new features"]},
        {"name": "Riley", "team": "Frontend", "commits": 16, "entropy": 0.88, "meetings": 4, "msgs": ["UI improvements", "Component refactor"]},
        {"name": "Avery", "team": "DevOps", "commits": 6, "entropy": 0.45, "meetings": 8, "msgs": ["Infrastructure update", "Monitoring setup"]}
      ],
      'Innovate Solutions': [
        {"name": "Emma", "team": "Frontend", "commits": 22, "entropy": 0.92, "meetings": 3, "msgs": ["React optimization", "New component library"]},
        {"name": "Liam", "team": "QA", "commits": 8, "entropy": 0.55, "meetings": 12, "msgs": ["Test automation", "Bug verification"]},
        {"name": "Sophia", "team": "Frontend", "commits": 15, "entropy": 0.78, "meetings": 6, "msgs": ["CSS improvements", "Mobile responsive"]},
        {"name": "Noah", "team": "Mobile", "commits": 19, "entropy": 0.89, "meetings": 4, "msgs": ["iOS deployment", "Android fixes"]},
        {"name": "Olivia", "team": "QA", "commits": 5, "entropy": 0.35, "meetings": 14, "msgs": ["Manual testing", "Quality reports"]},
        {"name": "William", "team": "Mobile", "commits": 13, "entropy": 0.72, "meetings": 7, "msgs": ["Flutter updates", "Performance tuning"]}
      ],
      'StartupIO': [
        {"name": "Ava", "team": "Full Stack", "commits": 25, "entropy": 0.95, "meetings": 2, "msgs": ["Full feature implementation", "Database optimization"]},
        {"name": "James", "team": "Data Science", "commits": 11, "entropy": 0.68, "meetings": 8, "msgs": ["ML model training", "Data analysis"]},
        {"name": "Isabella", "team": "Full Stack", "commits": 17, "entropy": 0.82, "meetings": 5, "msgs": ["API development", "Frontend integration"]},
        {"name": "Benjamin", "team": "Data Science", "commits": 9, "entropy": 0.58, "meetings": 10, "msgs": ["Statistical analysis", "Visualization tools"]},
        {"name": "Mia", "team": "Full Stack", "commits": 20, "entropy": 0.87, "meetings": 3, "msgs": ["System architecture", "Performance optimization"]}
      ]
    };

    return companyData[user.company] || [];
  };

  const value = {
    user,
    settings,
    login,
    logout,
    updateSettings,
    getMockData
  };

  return (
    <AppContext.Provider value={value}>
      {children}
    </AppContext.Provider>
  );
};

export default AppContext;
